import React from 'react';
import { Link } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { Drawer, Button, List, Divider, ListItem, ListItemIcon, ListItemText, Icon, IconButton } from '@material-ui/core';

const useStyles = makeStyles({
  menuIcon: {
    color: 'white',
    transition: 'transform .4s ease',
    '&:hover': {
      transform: 'scale(1.1)'
    },
  },
  list: {
    width: 250,
  },
  menuList: {
    marginTop: '30px',
  },
  fullList: {
    width: 'auto',
  },
});

const drawerList = [
  {
    text: 'Home',
    icon: 'home',
    link: '/'
  },
  // {
  //   text: 'Explore',
  //   icon: 'explore',
  //   link: '/explore'
  // },
  {
    text: 'Contact',
    icon: 'contact_phone',
    link: '/contact'
  },
  // {
  //   text: 'Blog',
  //   icon: 'vertical_split',
  //   link: '/blog'
  // }
]

export default function TemporaryDrawer() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    left: false,
  });

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, left: open });
  };

  const sideList = () => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer('left', false)}
      onKeyDown={toggleDrawer('left', false)}
    >
      <List className={classes.menuList}>

        {
          drawerList.map((item) => (

            <Link key={item.text} style={{textDecoration: 'none', color: 'black'}} to={item.link}>

              <ListItem button key={item.text}>

                <ListItemIcon>
                  <Icon>{item.icon}</Icon>
                </ListItemIcon>

                <ListItemText primary={item.text} />

              </ListItem>

            </Link>
          ))
        }

      </List>

    </div>
  );

  return (

    <div>

      <IconButton onClick={toggleDrawer('left', true)} >
        <Icon className={classes.menuIcon} fontSize="large">
          menu
        </Icon>
      </IconButton>

      <Drawer open={state.left} onClose={toggleDrawer('left', false)}>

        {sideList('left')}

      </Drawer>

    </div>
  );
}
