import React, { useState, createContext } from 'react';

export const EditorContext = createContext();

export const EditorProvider = props => {

    const [input, setInput] = useState('');
    const [styling, setStyling] = useState({
        bold: false,
        italic: false,
        underline: false,
        color: false,
        highlight: false,
    });
    const [font, setFont] = useState({
        arial: false,
        georgia: false,
        impact: false
    });

    const handleFont = ( newFont ) => {
        switch (newFont) {
            case 'arial':
                return setFont({ ...font, arial: !font.arial });
            case 'georgia':
                return setFont({ ...font, georgia: !font.georgia });
            case 'impact':
                return setFont({ ...font, impact: !font.impact});
            default:
                return
        };
    };

    const handleInput = (e) => {
        setInput(e.target.value);
    }

    const handleStyling = ( style ) => {
        switch (style) {
            case 'bold':
                return setStyling({ ...styling, bold: !styling.bold });
            case 'italic':
                return setStyling({ ...styling, italic: !styling.italic });
            case 'underline':
                return setStyling({ ...styling, underline: !styling.underline });
            case 'color':
                return setStyling({ ...styling, color: !styling.color });
            case 'highlight':
                return setStyling({ ...styling, highlight: !styling.highlight });
            default:
                return
        };
    };

    return (
        <EditorContext.Provider
            value={{
                handleInput,
                input,
                styling,
                handleStyling,
                font,
                handleFont
            }}
        >
            {props.children}
        </EditorContext.Provider>
    );
};
