import React, { Fragment, useContext } from 'react';

import { Typography, TextField, Icon } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Margin = () => {

    const holder = useContext(EditorContext);

    const marginTopSwitch = (trigger) => {
        console.log('TOP')
        if (trigger === 'value') {
            switch (holder.sectionMode.el) {
                case 'body' || 'image':
                    return holder.body[holder.sectionMode.index].marginTop;
                case 'title' || 'jumbotron':
                    return holder[holder.sectionMode.el].marginTop;
                default:
                    return <Icon>lock</Icon>;
            }
        } else {
            switch (holder.sectionMode.el) {
                case 'body' || 'image':
                    return false;
                case 'title' || 'jumbotron':
                    return false;
                default:
                    return true;
            }
        }
    };

    const marginBottomSwitch = (trigger) => {
        console.log('BOTTOM')
        if (trigger === 'value') {
            switch (holder.sectionMode.el) {
                case 'body' || 'image':
                    return holder.body[holder.sectionMode.index].marginBottom;
                case 'title' || 'jumbotron':
                    return holder[holder.sectionMode.el].marginBottom;
                default:
                    return <Icon>lock</Icon>;
            }
        } else {
            switch (holder.sectionMode.el) {
                case 'body' || 'image':
                    return false;
                case 'title' || 'jumbotron':
                    return false;
                default:
                    return true;
            }
        }
    };

    return (
        <Fragment>

            <Typography>
                Margin Top:
            </Typography>

            <TextField
                disabled={marginTopSwitch('disable')}
                style={{ width: 'auto' }}
                value={marginTopSwitch('value')}
                onChange={holder.handleMarginTop}
            />

            <Typography>
                Margin Bottom:
            </Typography>

            <TextField
                disabled={marginTopSwitch('disable')}
                style={{ width: 'auto' }}
                value={marginBottomSwitch('value')}
                onChange={holder.handleMarginBottom}
            />

        </Fragment>
    );
};

export default Margin;
