import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Drawer, CssBaseline, Toolbar, List, Typography, Divider, ListItem, ListItemIcon, ListItemText, Icon, ExpansionPanel, ExpansionPanelSummary, ExpansionPanelDetails } from '@material-ui/core';

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    zIndex: 20
  },
  appBar: {
    zIndex: 20,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
    zIndex: 20,
    position: 'relative',

  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  toolbar: theme.mixins.toolbar,
}));

const todoList = [
  {
    text: 'Attractions',
    icon: 'camera_alt',
    expansion: 'true',
    content: renderAttractions()
  },
  {
    text: 'Events',
    icon: 'event_note'
  },
  {
    text: 'Entertainment and Amusements',
    icon: 'local_movies'
  },
  {
    text: 'Choose & Cut Christmas Trees',
    icon: 'ac_unit'
  },
  {
    text: 'Guide to Game Day Parking',
    icon: 'where_to_vote',
  },
  {
    text: 'Shopping',
    icon: 'shopping_cart'
  },
  {
    text: 'Grandfather Mountain',
    icon: 'filter_hdr'
  },
  {
    text: 'Explore Boone Area Breweries',
    icon: 'local_drink'
  },
  {
    text: 'Travel Itineraries',
    icon: 'assignment'
  },
  {
    text: 'History Travels & Tours',
    icon: 'directions_bus'
  },
  {
    text: 'Art Galleries & Museums',
    icon: 'palette'
  },
  {
    text: 'Parks & Playgrounds',
    icon: 'nature_people'
  },
  {
    text: 'Spas & Wellness',
    icon: 'spa'
  },
  {
    text: 'Bon Appetit Appelachia',
    icon: 'local_dining'
  }
]

export default function ClippedDrawer() {
  const classes = useStyles();

  function renderAttractions() {
    console.log('IT WORKS');
    // <ExpansionPanel>
    //           <ExpansionPanelSummary
    //             expandIcon={<ExpandMoreIcon />}
    //             aria-controls="panel1a-content"
    //             id="panel1a-header"
    //           >
    //             <Typography className={classes.heading}>{item.text}</Typography>
    //           </ExpansionPanelSummary>
    //           <ExpansionPanelDetails>
    //             <Typography>

    //             </Typography>
    //           </ExpansionPanelDetails>
    //         </ExpansionPanel>
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.toolbar} />
        <List>
          {todoList.map((item, index) => (
            item.expansion ? 

            <ListItem button key={item.text}>
              <ListItemIcon><Icon>{item.icon}</Icon></ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>

            : 

            item.content()

          ))}
        </List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar} />

      </main>
    </div>
  );
}
