import React, { useEffect, useState, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Avatar } from '@material-ui/core';

import Nav from '../../components/Navigation/Nav';

const useStyles = makeStyles((theme) => ({
    container: {
        marginTop: 120,
        marginBottom: 80,
    },
    articlesContainer: {
        paddingLeft: '20%',
        paddingRight: 40,
    },
}));

const Article = ({ match }) => {

    const [article, setArticle] = useState('');

    useEffect(() => {
        const title = match.params.title;
        fetch('/api/editors/getArticle', {
            method: 'POST',
            body: JSON.stringify({ title }),
            headers: { 'Content-Type': 'application/json' },
        })
            .then(res => res.json())
            .then((result) => { setArticle(result); })
            .catch((error) => { console.log(error); });
    }, []);

    const classes = useStyles();

    if (!article) { return '' }
    return (
        <Grid container>

            <Grid style={{ height: 60 }} item xs={12}>

                <Nav />

            </Grid>
            <Grid item xs={12}>

                <Typography>
                    {article.title.text}
                </Typography>

                <Typography>
                    Description goes here
                </Typography>

                <div>

                    <Avatar
                        src={`https://media.licdn.com/dms/image/C4E03AQESXvxigX8NfQ/profile-displayphoto-shrink_800_800/0?e=1571875200&v=beta&t=jVs0jK8YKBHrOOcmnghTMtN9bhPfu7rH9MHADyTboBY`}
                        alt="Terri Banner Profile Photo"
                    />

                    <Typography>
                        Aug 21 * 4 min read
                    </Typography>

                </div>

                <img
                    src={article.jumbotron.src}
                    alt={article.title.text}
                />

                <Typography>
                    By Terri Banner
                </Typography>

                {
                    article.body.map((section) => {
                        if (section.isText) {
                            return (
                                <Fragment>

                                    <Typography>
                                        {section.text}
                                    </Typography>

                                </Fragment>
                            );
                        }
                        return <img src={section.src} alt={section.alt} />
                    })
                }

            </Grid>

        </Grid>
    );
};

export default Article;
