import React, {
    Fragment,
    useContext,
} from 'react';

import {
    Typography,
    TextField,
    Icon,
} from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Margin = (props) => {

    const holder = useContext(EditorContext);

    const marginSwitch = () => {
        switch (holder.sectionMode.el) {
            case 'body':
                return true;
            case 'image':
                return true;
            case 'title':
                return true;
            case' jumbotron':
                return true;
            default:
                return false;
        }
    };

    return (
        <Fragment>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    flexWrap: 'wrap',
                }}
            >

                <Typography
                    style={{ marginRight: props.margin }}
                    variant={props.xs ? 'body1' : 'h6'}
                >
                    Margin Top:
                </Typography>

                {
                    marginSwitch()
                        ?
                    <TextField
                        style={{ width: 40 }}
                        value={
                            holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                                ?
                            holder.body[holder.sectionMode.index].marginTop
                                :
                            holder[holder.sectionMode.el].marginTop
                        }
                        onChange={holder.handleMarginTop}
                    />
                        :
                    <Icon
                        style={{ width: 40 }}
                        fontSize={props.xs ? 'small' : 'large'}
                    >
                        lock
                    </Icon>
                }

                <Typography
                    style={{ margin: `0px ${props.margin}` }}
                    variant={props.xs ? 'body1' : 'h6'}
                >
                    Margin Bottom:
                </Typography>

                {
                    marginSwitch()
                        ?
                    <TextField
                        style={{ width: 40 }}
                        value={
                            holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                                ?
                            holder.body[holder.sectionMode.index].marginBottom
                                :
                            holder[holder.sectionMode.el].marginBottom
                        }
                        onChange={holder.handleMarginBottom}
                    />
                        :
                    <Icon
                        style={{ width: 40 }}
                        fontSize={props.xs ? 'small' : 'large'}
                    >
                        lock
                    </Icon>
                }

            </div>

        </Fragment>
    );
};

export default Margin;
