import React, { useState, useContext, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Toolbar, Typography, Modal, Button }from '@material-ui/core';

import { MediaContext } from '../../Context/MediaQuery';

import Drawer from './Drawer';
import Login from '../Login/Login';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    zIndex: 5,
  },
  appBar: {
    position: 'relative',
    background: 'linear-gradient(45deg, #986243 30%, #984843 90%)',
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    position: 'absolute',
    left: '50%',
    top: '50%',
    transform: 'translate(-50%, -50%)',
  },
  imgLg: {
    height: 70,
    width: 70,
    marginTop: 5
  },
  imgSm: {
    height: 50,
    width: 50,
    marginTop: 5
  }
}));

const Nav = (props) => {

    const classes = useStyles();
    const media = useContext(MediaContext);

    const [open, setOpen] = useState(false);

    const handleOpen = () => {setOpen(true); };
    const handleClose = () => { setOpen(false); };

    if (props.isBlog) {
      return (
        <div className={classes.root}>

          <AppBar className={classes.appBar} position="fixed">

            <Toolbar>

              <Drawer query={media.xs} />

              <Typography
                className={classes.title}
                variant={media.xs ? 'body1' : 'h5'}
                align="center"
              >
                A Mountain Dream
              </Typography>

              {
                media.xs
                  ?
                ''
                  :
                <Fragment>

                  <Button onClick={handleOpen}>
                    <Typography style={{ color: 'white' }} variant={media.xs ? 'subtitle2' : 'h5'}>
                      Login
                    </Typography>
                  </Button>

                  <Modal
                    open={open}
                    onClose={handleClose}
                  >
                    <Login close={handleClose} />
                  </Modal>

                </Fragment>
              }

            </Toolbar>

          </AppBar>

        </div>
      );
    }
    return (
        <div className={classes.root}>

          <AppBar className={classes.appBar} position="fixed">

            <Toolbar>

              <Drawer query={media.xs} />

              <Typography
                className={classes.title}
                variant={media.xs ? 'body1' : 'h5'}
                align="center"
              >
                A Mountain Dream
              </Typography>

            </Toolbar>

          </AppBar>

        </div>
    );
};

export default Nav;
