import React, { useContext } from 'react';

import TextSize from './Tools/TextSize';
import Font from './Tools/Font';
import Styling from './Tools/Styling';
import Justify from './Tools/Justify';
import Margin from './Tools/Margin';

import { EditorContext } from '../../Context/EditorContext';

import { makeStyles } from '@material-ui/core';
import { Grid, Button } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    container: {
        padding: 20,
        background: '#ffffff',
        position: 'fixed',
        top: 70,
        zIndex: 2,
        boxShadow: '0px 2px 4px -1px rgba(0,0,0,0.2), 0px 4px 5px 0px rgba(0,0,0,0.14), 0px 1px 10px 0px rgba(0,0,0,0.12)',
    },
    editorTools: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    input: {
        width: '80%'
    },
    save: {
      backgroundColor: 'rgb(0, 0, 0, 0.1)',
    },
    publish: {
        backgroundColor: 'rgb(0, 0, 0, 0.1)',
        padding: 10,
        '&:hover': {
            backgroundColor: 'rgb(0, 0, 0, 0.05)',
        },
    },
}));

const Toolbar = () => {

    const classes = useStyles();
    const holder = useContext(EditorContext);

    return (
        <Grid className={classes.container} justify="center" alignItems="center" container>

            <Grid item xs={1}>

                    {
                        holder.title.isPublished
                            ?
                        <Button className={classes.save} onClick={holder.handleChanges}>
                            Save
                        </Button>
                            :
                        <Button className={classes.publish} onClick={holder.handleSubmit}>
                            Publish
                        </Button>
                    }

            </Grid>
            <Grid className={classes.editorTools} item xs={11}>

                <TextSize />

                <Font />

                <Styling />

                <Justify />

                <Margin />

            </Grid>

        </Grid>
    );
};

export default Toolbar;
