import React, { useContext } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography } from '@material-ui/core';

import { Parallax, Background } from 'react-parallax';

import { MediaContext } from '../../../Context/MediaQuery';

import SlideShow from './SlideShow';



function Body() {

    const media = useContext(MediaContext);

    const useStyles = makeStyles(theme => ({
        container: {
            [theme.breakpoints.down('md')]: {
                height:'140vh',
            },
            height: '100vh',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'flex-end',
            flexWrap: 'wrap',
        },
        parallaxSlideshow: {
            [theme.breakpoints.down('md')]: {
                overflow: 'visible !important',
            },
            width: '100%', 
            height: '50%',
            '& .react-parallax-background-children': {
                top: media.xs ? '30%'
                :
                media.sm ? '30%'
                    :
                media.md ? '-30%'
                    :
                media.lg ? '40%'
                    :
                '40%'
            },
        },
        parallaxText: {
            [theme.breakpoints.down('md')]: {
                overflow: 'visible !important',
            },
            width: '100%', 
            height: '50%',
            '& .react-parallax-background-children': {
                top: media.xs ? '30%'
                :
                media.sm ? '35%'
                    :
                media.md ? '120%'
                    :
                media.lg ? '45%'
                    :
                '50%',
                width: '100%', 
            },
        },
        paper: {
            width: '70%',
            margin: '80px 0px',
            padding: '20px 0px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexWrap: 'wrap',
            background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
        },
        typoContainer: {
            width: '80%',
            padding: 20,
        },
        textContainer: {
            height: '50%',
            width: '100%',
            padding: 20,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
        },
        slideContainer: {
            height: '100%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        },
    }));

    const classes = useStyles();

    return (

        <Grid className={classes.container} container>

            <Grid className={classes.textContainer} item lg={6} md={12}>     

                <Parallax className={classes.parallaxText} strength={media.md ? 400 : -150}>

                    <Background>

                        <Typography variant="h5" align="center">
                            {`A Mountain Dream Cabin is far enough from 
                            civilization to be secluded, yet close enough to town & 
                            local attractions to be more than convenient. Located only 
                            10 quick miles outside of Boone, NC in the exclusive log 
                            cabin community of Twin Hollows. A Mountain Dream has every 
                            amenity to make your stay as comfortable, pleasant and stress 
                            free as you could ever imagine.`}
                        </Typography>

                    </Background>

                </Parallax>

            </Grid>
            <Grid className={classes.slideContainer} item lg={6} md={12}>

                <Parallax 
                    className={classes.parallaxSlideshow}
                    strength={media.md ? -350 : 350}
                >

                    <Background style={{ width: '100%' }}>

                        <SlideShow />

                    </Background>

                </Parallax>

            </Grid>

        </Grid>
    );
}

export default Body;
