import React, { useContext } from 'react';
import { Link } from 'react-router-dom';

import { makeStyles } from '@material-ui/core/styles';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Icon, IconButton } from '@material-ui/core';

import { MediaContext } from '../../Context/MediaQuery';

const useStyles = makeStyles(theme => ({
  drawer: {
    background: 'linear-gradient(135deg, rgb(26, 118, 30) 0%, rgb(26, 118, 30) 23%,rgb(72, 215, 80) 23%, rgb(72, 215, 80) 28%,rgb(34, 176, 39) 28%, rgb(34, 176, 39) 35%,rgb(42, 126, 41) 35%, rgb(42, 126, 41) 100%)',
  },
  menuIcon: {
    color: 'white',
    transition: 'transform .4s ease',
    '&:hover': {
      transform: 'scale(1.1)'
    },
  },
  list: {
    [theme.breakpoints.up('lg')]: {
      width: 300
    },
    width: 250,
  },
  listItems: {
    '&:hover': {
      transform: 'translate(20px, 0px)'
    },
    transition: 'transform .4s ease',
    marginBottom: 20,
    color: 'white'
  },
  menuList: {
    marginTop: '30px',
  },
  fullList: {
    width: 'auto',
  },
}));

const drawerList = [
  {
    text: 'Home',
    icon: 'home',
    link: '/'
  },
  // {
  //   text: 'Explore',
  //   icon: 'explore',
  //   link: '/explore'
  // },
  {
    text: 'Contact',
    icon: 'contact_phone',
    link: '/contact'
  },
  // {
  //   text: 'Blog',
  //   icon: 'vertical_split',
  //   link: '/blog'
  // }
]

const TemporaryDrawer = props => {

  const classes = useStyles();
  const media = useContext(MediaContext);

  const [state, setState] = React.useState({
    left: false,
  });

  const toggleDrawer = (side, open) => event => {

    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    else {
      setState({ ...state, left: open });
    }
  };

  const sideList = () => (

    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer('left', false)}
      onKeyDown={toggleDrawer('left', false)}
    >
      <List className={classes.menuList}>

        {
          drawerList.map((item) => (

            <Link key={item.text} style={{textDecoration: 'none', color: 'black'}} to={item.link}>

              <ListItem className={classes.listItems} button key={item.text}>

                <ListItemIcon>
                  <Icon style={{ color: 'white' }} fontSize={!media.md ? 'large' : 'small'}>{item.icon}</Icon>
                </ListItemIcon>

                <ListItemText 
                  primary={item.text} 
                  primaryTypographyProps={{
                    variant: !media.md ? 'h4' : 'h6',
                  }}
                />

              </ListItem>

            </Link>
          ))
        }

      </List>

    </div>
  );

  return (

    <div>

      <IconButton onClick={toggleDrawer('left', true)}>

        <Icon className={classes.menuIcon} fontSize={props.query ? 'small' : 'large'}>
          menu
        </Icon>

      </IconButton>

      <Drawer 
        PaperProps={{ 
          style: {
            background: 'linear-gradient(135deg, rgb(26, 118, 30) 0%, rgb(26, 118, 30) 23%,rgb(72, 215, 80) 23%, rgb(72, 215, 80) 28%,rgb(34, 176, 39) 28%, rgb(34, 176, 39) 35%,rgb(42, 126, 41) 35%, rgb(42, 126, 41) 100%)',
          }
        }}
        open={state.left} 
        onClose={toggleDrawer('left', false)}
      >

        {sideList('left')}

      </Drawer>

    </div>
  );
};

export default TemporaryDrawer;
