import React, { useContext, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Typography, Avatar } from '@material-ui/core';

import { EditorContext } from '../../Context/EditorContext';

const useStyles = makeStyles((theme) => ({
    infoContainer: {
        position: 'relative',
        height: 60,
        width: 'auto',
        paddingLeft: 80,
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        flexBasis: 'auto',
    },
    avatar: {
        height: 60,
        width: 60,
        position: 'absolute',
        top: 0,
        left: 0,
    },
}));

const NewArticle = () => {

    const holder = useContext(EditorContext);
    const classes = useStyles();

    if (holder.articleList) {
        return (
            <Fragment>

                <img
                    style={{ width: '100%', height: 'auto' }}
                    src={holder.articleList[0].jumbotron.src}
                    alt={holder.articleList[0].title.text}
                />

                <Typography style={{ marginTop: 30 }} variant="h2">
                    {holder.articleList[0].title.text}
                </Typography>

                <Typography style={{ marginTop: 20, marginBottom: 20 }} variant="h6" color="textSecondary">
                    Description will go here
                </Typography>

                <div className={classes.infoContainer}>

                    <Avatar
                        className={classes.avatar}
                        src={`https://media.licdn.com/dms/image/C4E03AQESXvxigX8NfQ/profile-displayphoto-shrink_800_800/0?e=1571875200&v=beta&t=jVs0jK8YKBHrOOcmnghTMtN9bhPfu7rH9MHADyTboBY`}
                        alt="Terri Banner Profile Photo"
                    />

                    <Typography variant="body1">
                        Terri Banner
                    </Typography>

                    <Typography variant="body1" color="textSecondary">
                        Aug 21 * 2 min read
                    </Typography>

                </div>

            </Fragment>
        );
    } else { return '' }
};

export default NewArticle;
