import React, { useState, createContext, useEffect } from 'react';

export const EditorContext = createContext();

const initialStyling = {
    bold: false,
    italic: false,
    underline: false,
    color: 'black',
    highlight: false,
}

export const EditorProvider = props => {

    const [input, setInput] = useState('');
    const [article, setArticle] = useState([]);
    const [styling, setStyling] = useState(initialStyling);
    const [font, setFont] = useState( 'Roboto' );
    const [justify, setJustify] = useState('left');
    const [textStyle, setTextStyle] = useState('body1');

    useEffect(() => {
        console.log(textStyle);
    }, [textStyle]);


    const handleInput = (e) => {
        setInput(e.target.value);
    };

    const handleArticle = () => {
        let newArticle = article;
        let section = {
            text: input,
            font: font,
            justify: justify,
            textStyle: textStyle,
            bold: styling.bold,
            italic: styling.italic,
            underline: styling.underline,
            color: styling.color,
            highlight: styling.highlight
        }
        newArticle.push(section);

        setArticle(newArticle);
        setInput('');
        setStyling(initialStyling);
        setFont('Roboto');
        setJustify('left');
        setTextStyle('body1');

    };

    const handleFont = ( newFont ) => {
        setFont(newFont);
    };

    const handleStyling = ( style, color ) => {
        switch (style) {
            case 'bold':
                return setStyling({ ...styling, bold: !styling.bold });
            case 'italic':
                return setStyling({ ...styling, italic: !styling.italic });
            case 'underline':
                return setStyling({ ...styling, underline: !styling.underline });
            case 'color':
                return setStyling({ ...styling, color: color });
            case 'highlight':
                return setStyling({ ...styling, highlight: !styling.highlight });
            default:
                return
        };
    };

    const handleJustify = justification => {
        setJustify(justification);
    };

    const handleTextStyle = style => {
        setTextStyle(style);
    };

    return (
        <EditorContext.Provider
            value={{
                handleInput,
                input,
                styling,
                handleStyling,
                font,
                handleFont,
                justify,
                handleJustify,
                textStyle,
                handleTextStyle,
                article,
                handleArticle
            }}
        >
            {props.children}
        </EditorContext.Provider>
    );
};
