import React, { useState, createContext, useEffect } from 'react';

export const EditorContext = createContext();

const initialStyling = {
}

const intialPreview = {
    text: '',
    bold: false,
    italic: false,
    underline: false,
    color: 'black',
    highlight: false,
    font: 'Roboto',
    justify: 'left',
    textStyle: 'body1',
}

export const EditorProvider = props => {

    const [editMode, setEditMode] = useState(false);
    const [articleList, setArticleList] = useState([]);

    const [sectionMode, setSectionMode] = useState('title');
    const [title, setTitle] = useState({ ...intialPreview });
    const [jumbotron, setJumbotron] = useState('');
    const [currentBody, setCurrentBody] = useState({ ...intialPreview });
    const [body, setBody] = useState([ { ...intialPreview } ]);

    useEffect(() => {

        fetch('/api/editors/getList')
        .then(res => res.json())
        .then(result => {
            console.log(...result);
            setArticleList(result)
        })
        .catch(error => console.log(error));

    }, [] )

    useEffect(() => {
        console.log(title);
    })

    const handleMode = () => {
        setEditMode(!editMode);
    }

    const handleSectionMode = (newSection) => {
        switch (newSection) {

            case 'title':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            case 'jumbotron':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            case 'body':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            default:
                return
        }
    };

    const handleSubmit = data => {

        fetch('/api/editors/newSection',{
            method: 'POST',
            body: JSON.stringify(data),
            headers: {'Content-Type': 'application/json'}
        })
        .then(res => res.json())
        .then((result) => {
            console.log(result);
            setTitle(result.title);
            setJumbotron(result.jumbotron);
            setBody(result.body);
        })
        .catch(error => {
            console.log(error);
        })
    };

    const handleInput = (e) => {

        let input = e.target.value;

        setTitle({ ...title, text: input });
    };

    const handleArticle = () => {

        let section = {
            sectionMode: sectionMode,
            title: title,
            jumbotron: jumbotron,
            text: body,
        }

        handleSubmit(section);
    };

    const handleFont = ( newFont ) => {

        if(sectionMode === 'title') {

            setTitle({ ...title, font: newFont });
        }
        else {

            setCurrentBody({ ...currentBody, font: body });
        }
    };

    const handleStyling = ( style, color ) => {

        if(sectionMode === 'title') {

            setTitle({ ...title, [style]: !title[style] });
        }
        else {

            setCurrentBody({ ...currentBody, [style]: !body[style] });   
        }
    };

    const handleJustify = justification => {

        if(sectionMode === 'title') {

            setTitle({ ...title, justify: justification });
        }
        else {

            setCurrentBody({ ...currentBody, justify: justification });
        }
    };

    const handleTextStyle = style => {

        if(sectionMode === 'title') {

            setTitle({ ...title, textStyle: style });
        }
        else {

            setCurrentBody({ ...currentBody, textStyle: style });
        }
    };

    return (
        <EditorContext.Provider
            value={{
                handleInput,
                title,
                jumbotron,
                currentBody,
                body,
                handleStyling,
                handleFont,
                handleJustify,
                handleTextStyle,
                handleArticle,
                editMode,
                handleMode,
                articleList,
                sectionMode,
                handleSectionMode
            }}
        >
            {props.children}
        </EditorContext.Provider>
    );
};
