import React from 'react';

import { TextField } from '@material-ui/core';

export default ({ body, xs, fonts, index, inputClass, handleSectionMode, handleInput }) => {

    console.log(body);
    return (
        <TextField
            style={{
                width: '100%',
                marginTop: parseInt(body.marginTop),
                marginBottom: parseInt(body.marginBottom),
            }}
            multiline={true}
            inputProps={{
                style: {
                    width: '100%',
                    fontSize: xs ? fonts.mobileFontSizes[body.textStyle] : fonts.fontSizes[body[index].textStyle],
                    fontFamily: `${body[index].font}, Helvetica, Arial, sans-serif`,
                    fontWeight: body[index].bold ? 'bold' : 'normal',
                    fontStyle: body[index].italic ? 'italic' : 'normal',
                    textDecoration: body[index].underline ? 'underline' : 'none',
                    textAlign: body[index].justify,
                    color: body[index].color,
                    backgroundColor: body[index].highlight ? '#ffff00' : 'inherit',
                    lineHeight: 1.17,
                } 
            }}
            InputProps={{ className: inputClass }}
            value={body[index].text}
            onClick={() => handleSectionMode({ el: 'body', index: index })}
            onChange={(e) => handleInput(e.target.value, { El: 'body', index: index })}
            key={index}
        />
    );
};
