import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Paper } from '@material-ui/core';

import { Parallax } from 'react-parallax';

import Stepper from './Stepper/Stepper';
import ImageList from './ImageList.js';

import Background from './imgs/1x/footer.png';
import BRP from './imgs/BRP.jpg';

const useStyles = makeStyles(theme => ({
    mainContainer: {
        // backgroundImage: `url(${Background})`,
        // backgroundSize: 'cover',
        // height: '200vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
    },
    topGradient: {
        background: 'linear-gradient(0deg, rgb(51, 138, 249),rgb(0, 0, 0, 0))',
        height: '20vh',
    },
    headerContainer: {
        // marginTop: 620,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        alignContent: 'center',
        flexWrap: 'wrap',
        background: 'linear-gradient(0deg, rgb(33, 110, 209),rgb(51, 138, 249))',
    },
    header: {
        color: 'white',
        margin: '40px 0px',
    }, 
    parallaxContainer: {
        margin: '100px 0px 60px 0px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
    },
    paper: {
        padding: 30,
        width: '70%',
        background: 'rgb(0, 0, 0, 0.05)',
    },
    brpDesc: {
        color: 'white',
        marginTop: 20
    },
}));

const Footer = () => {

    const classes = useStyles();

    return (

        <Grid className={classes.mainContainer} container>

            <Grid className={classes.topGradient} item xs={12}>

            </Grid>

            <Grid className={classes.headerContainer} item xs={12}>

                <Typography className={classes.header} variant="h1" align="center">
                    We're right by the Blue Ridge Parkway
                </Typography>

                <ImageList />


                <Stepper />

            </Grid>

        </Grid>

    );
};

export default Footer;
