import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, GridList, GridListTile } from '@material-ui/core';

import { Parallax, Background } from 'react-parallax';

const tileData = [
    {
        img: `https://assets.simpleviewinc.com/simpleview/image/fetch/c_fill,h_333,q_75,w_500/https://assets.simpleviewcms.com/simpleview/image/upload/v1/clients/asheville/hiking_black_balsam_jaredkay_untitled_105_1379b8e6-a3ad-4761-83ec-dc25882f4b34.jpg`,
        title: 'Blue Ride Parkway',
        author: '',
        cols: 2,
        strength: 100
    },
    {
        img: `https://blueridgetravelguide.com/custom/domain_1/image_files/1_photo_1043.jpg`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 1,
        strength: 150
    },
    {
        img: `https://www.nps.gov/articles/images/BLRI_Standing_Rock_Overlook_nocover.jpg?maxwidth=650&autorotate=false`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 1,
        strength: 100
    },
    {
        img: `http://www.daveallenphotography.com/gallery/l_summer_laurel_blue_ridge_parkway_nc.jpg`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 1,
        strength: 150
    },
    {
        img: `https://photorator.com/photos/images/burnett-reservoir-as-viewed-from-above-on-the-blue-ridge-parkway-north-carolina--81832.jpg`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 1,
        strength: 100
    },
    {
        img:  `https://www.blueridgemountains.com/imager/s3_amazonaws_com/blueridge-2018/images/1920-width/iStock-134561164_5ac8ae049534b01c104dc4019c5230ff.jpg`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 1,
        strength: 150
    },
    {
        img: `https://www.romanticasheville.com/sites/default/files/u13/linville-falls-autumn.jpg`,
        title: 'Blue Ridge Parkway',
        author: '',
        cols: 2,
        strength: 100
    }
]

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        backgroundColor: theme.palette.background.paper,
    },
    gridList: {
        width: 500,
        height: 500,
    },
    parallax: {
        height: '100%',
        width: '100%'
    },
}));

const ImageList = () => {

    const classes = useStyles();

    return (

        <div className={classes.root}>

            <GridList cellHeight={160} className={classes.gridList} cols={3}>

            {
                tileData.map(tile => (

                    <GridListTile key={tile.img} cols={tile.cols || 1}>

                    <Parallax 
                        className={classes.parallax} 
                        strength={tile.strength}
                        bgImageStyle={{top: '-40%'}}
                    >

                        <Background className={classes.parallax}>

                            <img src={tile.img} alt={tile.title} />

                        </Background>

                    </Parallax>                 

                    </GridListTile>

                ))
            }

            </GridList>

        </div>

    );
};

export default ImageList;
