import React, { useState } from 'react';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import { Grid, MobileStepper, Button, Icon, Paper, Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    descContainer: {
        margin: '20px 0px 60px 0px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap'
    },
    paper: {
        width: '70%',
        background: 'linear-gradient(238deg, rgba(70, 70, 70, 0.09) 0%, rgba(70, 70, 70, 0.09) 50%,rgba(214, 214, 214, 0.09) 50%, rgba(214, 214, 214, 0.09) 100%),linear-gradient(126deg, rgba(223, 223, 223, 0.05) 0%, rgba(223, 223, 223, 0.05) 50%,rgba(217, 217, 217, 0.05) 50%, rgba(217, 217, 217, 0.05) 100%),linear-gradient(152deg, rgba(116, 116, 116, 0.08) 0%, rgba(116, 116, 116, 0.08) 50%,rgba(248, 248, 248, 0.08) 50%, rgba(248, 248, 248, 0.08) 100%),linear-gradient(225deg, rgba(1, 1, 1, 0.07) 0%, rgba(1, 1, 1, 0.07) 50%,rgba(5, 5, 5, 0.07) 50%, rgba(5, 5, 5, 0.07) 100%),linear-gradient(194deg, rgba(14, 14, 14, 0.09) 0%, rgba(14, 14, 14, 0.09) 50%,rgba(206, 206, 206, 0.09) 50%, rgba(206, 206, 206, 0.09) 100%),linear-gradient(100deg, rgba(220, 220, 220, 0.07) 0%, rgba(220, 220, 220, 0.07) 50%,rgba(65, 65, 65, 0.07) 50%, rgba(65, 65, 65, 0.07) 100%),linear-gradient(190deg, rgba(194, 194, 194, 0.03) 0%, rgba(194, 194, 194, 0.03) 50%,rgba(206, 206, 206, 0.03) 50%, rgba(206, 206, 206, 0.03) 100%),linear-gradient(320deg, rgba(10, 10, 10, 0.08) 0%, rgba(10, 10, 10, 0.08) 50%,rgba(231, 231, 231, 0.08) 50%, rgba(231, 231, 231, 0.08) 100%),linear-gradient(90deg, rgb(10, 147, 39),rgb(235, 252, 123))',
        color: 'white',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-end',
        flexWrap: 'wrap'
    },
    brpDesc: {
        padding: 30,
    },
    stepper: {
        padding: 10,
        width: '100%',
        background: 'linear-gradient(45deg, #155730 30%, #0c4222 90%)',
    },
    stepperDisplay: {
        color: 'white',
    },
    button: {
        padding: 15,
        background: 'rgba(0, 0, 0, 0.1)'
        // background: 'linear-gradient(45deg, #3796f0 30%, #0d72d1 90%)'
    },
}));

const Stepper = () => {

    const classes = useStyles();

    return (

            <Paper className={classes.paper}>

                <Typography variant="h6" align="center">
                    The Blue Ridge Parkway is a ribbon of road that weaves through 
                    the region’s vibrant living traditions. From Cherokee life and 
                    agricultural history to Southern Appalachian crafts and music, 
                    the Parkway’s 469 miles through Virginia and North Carolina offer 
                    many ways to explore rich traditions.
                </Typography>

                <Typography variant="h6" align="center">
                    Spanning the southern and central Appalachians, the Blue Ridge Parkway 
                    offers an exceptional glimpse of the regional flora and fauna. It is 
                    world-renowned for its biodiversity. The Parkway covers a wide range 
                    of habitats along the Appalachian Mountains, and some of these habitats 
                    are exceptionally rare. Visitors encounter unsurpassed diversity of 
                    climate, vegetation, wildlife and geological features.
                </Typography>

                <Typography variant="h6" align="center">
                    Millions of visitors enjoy the Blue Ridge Parkway each year. 
                    Some think of it as just a motor road or a pleasant drive. But 
                    the Parkway is also a place of varied and significant natural 
                    resources.
                </Typography>

                <a style={{textDecoration: 'none'}} href={`https://www.blueridgeparkway.org/`} target="_blank">
                    <Button className={classes.button}>

                        <Typography style={{ color: '#6d82fc' }} variant="h4" align="center">
                            For More Information
                        </Typography>

                    </Button>
                </a>

            </Paper>


    );
};

export default Stepper;
