import React, { Fragment } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Paper, Typography } from '@material-ui/core';

import { Parallax, Background } from 'react-parallax';

import SlideShow from './SlideShow';


const useStyles = makeStyles(theme => ({
    container: {
        height: '80vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-end',
        flexWrap: 'wrap',
    },
    paper: {
        width: '70%',
        margin: '80px 0px',
        padding: '20px 0px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
        background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
    },
    typoContainer: {
        // background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
        width: '80%',
        padding: 20,
    },
    textContainer: {
        height: '100%',
        width: '100%',
        padding: 20,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    slideContainer: {
        height: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    slideText: {
        fontSize: '1.4rem',
        color: 'white',
    },
}));

function Body() {

    const classes = useStyles();

    return (
        <Fragment>

        <Grid className={classes.container} container>





                <Grid className={classes.textContainer} item xs={6}>     

                <Parallax 
                    style={{ width: '100%', height: '100%'}} 
                    strength={300}
                    bgStyle={{
                        width: '100%',
                        top: '40%',
                    }}
                >


                <Background style={{ width: '100%', height: '100%'}}>


                    <Typography className={classes.slideText} align="center">
                        {`A Mountain Dream Cabin is far enough from 
                        civilization to be secluded, yet close enough to town & 
                        local attractions to be more than convenient. Located only 
                        10 quick miles outside of Boone, NC in the exclusive log 
                        cabin community of Twin Hollows. A Mountain Dream has every 
                        amenity to make your stay as comfortable, pleasant and stress 
                        free as you could ever imagine.`}
                    </Typography>
                    </Background>
            </Parallax>


                </Grid>
                <Grid className={classes.slideContainer} item xs={6}>

                <Parallax 
                    style={{ width: '100%', height: '100%'}} 
                    strength={200}
                    bgStyle={{
                        width: '100%',
                        top: '40%',
                    }}
                >



                <Background style={{ width: '100%', height: '100%'}}>



                    <SlideShow />

                </Background>
                </Parallax>
                </Grid>


            </Grid>


        </Fragment>
    );
}

export default Body;
