import React, { Fragment, useState } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Drawer, CssBaseline, Toolbar, List, Typography, Divider, ListItem, ListItemIcon, ListItemText, Icon, ExpansionPanel, ExpansionPanelSummary, ExpansionPanelDetails } from '@material-ui/core';

import Attractions from './Activities/Attractions';
import Travel from './Activities/Travel';

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    zIndex: 20
  },
  appBar: {
    zIndex: 20,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
    zIndex: 20,
    position: 'relative',

  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  toolbar: theme.mixins.toolbar,
}));

const todoList = [
  {
    text: 'Attractions',
    icon: 'camera_alt',
    expansion: true,
    content: function() {
      return (
        <Fragment>
          <Attractions />
        </Fragment>
      )
    }
  },
  {
    text: 'Events',
    icon: 'event_note'
  },
  {
    text: 'Entertainment and Amusements',
    icon: 'local_movies'
  },
  {
    text: 'Choose & Cut Christmas Trees',
    icon: 'ac_unit'
  },
  {
    text: 'Guide to Game Day Parking',
    icon: 'where_to_vote',
  },
  {
    text: 'Shopping',
    icon: 'shopping_cart'
  },
  {
    text: 'Grandfather Mountain',
    icon: 'filter_hdr'
  },
  {
    text: 'Explore Boone Area Breweries',
    icon: 'local_drink'
  },
  {
    text: 'Travel Itineraries',
    icon: 'assignment',
    expansion: true,
    content: function() {
      return (
        <Travel />
      )
    }
  },
  {
    text: 'History Travels & Tours',
    icon: 'directions_bus'
  },
  {
    text: 'Art Galleries & Museums',
    icon: 'palette'
  },
  {
    text: 'Parks & Playgrounds',
    icon: 'nature_people'
  },
  {
    text: 'Spas & Wellness',
    icon: 'spa'
  },
  {
    text: 'Bon Appetit Appelachia',
    icon: 'local_dining'
  }
]

export default function ClippedDrawer() {
  const classes = useStyles();

  const [state, setState] = useState({
    generalStore: false,
    tweetsie: false,
    rockyBikePark: false,
    hornWest: false,
    blowingRock: false,
    mysteryHill: false,
    events: false,
    eNa: false,
    christmasTrees: false,
    gameParking: false,
    shopping: false,
    grandfather: false,
    breweries: false,
    wineTrail: false,
    longWeekends: false,
    backRoads: false,
    sojourn: false,
    romanticWeekend: false,
    historyTours: false,
    artGalleries: false,
    parks: false,
    spas: false,
    bonAppetit: false
  });

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.toolbar} />
        <List>
          {todoList.map((item, index) => (
            !item.expansion ? 

            <ListItem button onClick={(e) => console.log(e.target.innerText)} key={item.text}>
              <ListItemIcon><Icon>{item.icon}</Icon></ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>

            : 

            item.content()

          ))}
        </List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.toolbar} />

      </main>
    </div>
  );
}
