import React, { useContext, useEffect, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography } from '@material-ui/core';

import { EditorContext } from '../../Context/EditorContext';

const Preview = () => {

    const holder = useContext(EditorContext);

    const useStyles = makeStyles(theme => ({
        typo: {
            fontFamily: `${holder[holder.sectionMode].font}, Helvetica, Arial, sans-serif`,
            fontWeight: holder[holder.sectionMode].bold ? 'bold' : 'normal',
            fontStyle: holder[holder.sectionMode].italic ? 'italic' : 'normal',
            textDecoration: holder[holder.sectionMode].underline ? 'underline' : 'none',
            textAlign: holder[holder.sectionMode].justify,
            color: holder[holder.sectionMode].color,
            backgroundColor: holder[holder.sectionMode].highlight ? '#ffff00' : 'none'
        }
    }));

    const classes = useStyles();

    return (

        <Grid container>

            <Grid item xs={12}>



                <Typography className={classes.typo} variant={holder[holder.sectionMode].textStyle}>
                    {holder.title.text}
                </Typography>

                {
                    holder.jumbotron
                        ?
                    <img style={{ width: '100%', height: 'auto' }} src={holder.jumbotron} alt={holder.title} />
                        :
                    ''
                }

                { 
                    holder.body.map((section, index) => {
                        return (
                            <Typography
                                style={{
                                    fontFamily: `${section.font}, Helvetica, Arial, sans-serif`,
                                    fontWeight: section.bold ? 'bold' : 'normal',
                                    fontStyle: section.italic ? 'italic' : 'normal',
                                    textDecoration: section.underline ? 'underline' : 'none',
                                    color: section.color,
                                    backgroundColor: section.highlight ? '#ffff00' : 'none'                        
                                }}
                                variant={section.textStyle}
                                align={section.justify}
                                key={index}
                            >
                                {section.text}
                            </Typography>
                        )
                    })
                }

            </Grid>

        </Grid>
    );
};

export default Preview;
