import React, { useContext, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Typography, Divider } from '@material-ui/core';

import { EditorContext } from '../../Context/EditorContext';

const useStyles = makeStyles((theme) => ({
    containers: {
        position: 'relative',
        paddingRight: '20%',
    },
    imgs: {
        position: 'absolute',
        top: 0,
        right: 0,
        width: '20%',
        height: '100%',
    },
}));

const Latest = () => {

    const holder = useContext(EditorContext);
    const classes = useStyles();

    if (!holder.articleList) { return '' }
    return (
        <Fragment>

            <Typography variant="h5">
                Latest
            </Typography>

            <Divider />

            {
                holder.articleList.map((article, index) => {
                    if (index === 0) { return '' }
                    return (
                        <div className={classes.containers}>

                            <div
                                className={classes.imgs}
                                style={{
                                    backgroundImage: `url(${article.jumbotron.src})`,
                                    backgroundSize: 'cover',
                                }}
                            ></div>

                            <Typography style={{ marginTop: 20 }} variant="h2">
                                {article.title.text}
                            </Typography>

                            <Typography style={{ marginTop: 15, marginBottom: 15 }} variant="h6" color="textSecondary">
                                Description will go here
                            </Typography>

                            <Typography style={{ width: '100%' }} variant="body1">
                                Terri Banner
                            </Typography>

                            <Typography style={{ width: '100%' }} variant="body1" color="textSecondary">
                                Aug 21 * 2 min read
                            </Typography>

                        </div>
                    );
                })
            }

        </Fragment>
    );
};

export default Latest;
