import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Paper } from '@material-ui/core';

import { Parallax } from 'react-parallax';

import Stepper from './Stepper/Stepper';
import ImageList from './ImageList.js';

import Background from './imgs/1x/footer.png';
import BRP from './imgs/BRP.jpg';

const useStyles = makeStyles(theme => ({
    mainContainer: {
        backgroundImage: `url(${Background})`,
        backgroundSize: 'cover',
        // height: '200vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    headerContainer: {
        marginTop: 520,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    header: {
        color: 'white',
    }, 
    parallaxContainer: {
        margin: '100px 0px 60px 0px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
    },
    paper: {
        padding: 30,
        width: '70%',
        background: 'rgb(0, 0, 0, 0.05)',
    },
    brpDesc: {
        color: 'white',
        marginTop: 20
    },
}));

const Footer = () => {

    const classes = useStyles();

    return (

        <Grid className={classes.mainContainer} container>

            <Grid className={classes.headerContainer} item xs={12}>

                <Typography className={classes.header} variant="h1" align="center">
                    We're right by the Blue Ridge Parkway
                </Typography>

            </Grid>
            <Grid className={classes.parallaxContainer} item xs={12}>

                <ImageList />

            </Grid>
            <Grid className={classes.container} item xs={12}>

                <Stepper />

            </Grid>

        </Grid>

    );
};

export default Footer;
