import React, { Fragment, useContext } from 'react';

import { Typography, TextField, Icon } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Margin = () => {

    const holder = useContext(EditorContext);

    const marginTopSwitch = () => {
        console.log('TOP')
        switch (holder.sectionMode.el) {
            case 'body':
                return true;
            case 'image':
                return true;
            case 'title':
                return true;
            case' jumbotron':
                return true;
            default:
                return false;
        }
    };

    const marginBottomSwitch = () => {
        console.log('BOTTOM')
        switch (holder.sectionMode.el) {
            case 'body' || 'image':
                return <TextField
                            style={{ width: 'auto' }}
                            value={holder.body[holder.sectionMode.index].marginBottom}
                            onChange={holder.handleMarginBottom}
                        />;
            case 'title' || 'jumbotron':
                return <TextField
                            style={{ width: 'auto' }}
                            value={holder[holder.sectionMode.el].marginBottom}
                            onChange={holder.handleMarginBottom}
                        />;
            default:
                return <Icon>lock</Icon>;
        }
    };

    return (
        <Fragment>

            <Typography>
                Margin Top:
            </Typography>

            {
                marginTopSwitch()
                    ?
                <TextField
                    style={{ width: 'auto' }}
                    value={
                        holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                            ?
                        holder.body[holder.sectionMode.index].marginTop
                            :
                        holder[holder.sectionMode].marginTop
                    }
                    onChange={holder.handleMarginTop}
                />
                    :
                <Icon>lock</Icon>
            }

            <Typography>
                Margin Bottom:
            </Typography>

            {
                marginBottomSwitch()
                    ?
                <TextField
                    style={{ width: 'auto' }}
                    value={
                        holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                            ?
                        holder.body[holder.sectionMode.index].marginBottom
                            :
                        holder[holder.sectionMode].marginBottom
                    }
                    onChange={holder.handleMarginBottom}
                />
                    :
                <Icon>lock</Icon>
            }

        </Fragment>
    );
};

export default Margin;
