import React, { Fragment, useContext } from 'react';

import { Button, Icon } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Justify = () => {

    const holder = useContext(EditorContext);

    return (
        <Fragment>

            <Button
                disabled={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder.body[holder.sectionMode.index].justify
                        :
                    holder[holder.sectionMode.el].justify === 'left'
                }
                onClick={() => holder.handleJustify('left')}
            >
                <Icon>format_align_left</Icon>
            </Button>
            <Button
                disabled={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder.body[holder.sectionMode.index].justify
                        :
                    holder[holder.sectionMode.el].justify === 'center'
                }
                onClick={() => holder.handleJustify('center')}
            >
                <Icon>format_align_center</Icon>
            </Button>
            <Button
                disabled={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder.body[holder.sectionMode.index].justify
                        :
                    holder[holder.sectionMode.el].justify === 'right'
                }
                onClick={() => holder.handleJustify('right')}
            >
                <Icon>format_align_right</Icon>
            </Button>
            <Button
                disabled={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder.body[holder.sectionMode.index].justify
                        :
                    holder[holder.sectionMode.el].justify === 'justify'
                }
                onClick={() => holder.handleJustify('justify')}
            >
                <Icon>format_align_justify</Icon>
            </Button>

        </Fragment>
    );
};

export default Justify;
