import React, { useContext } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Button, Divider, Typography, Paper } from '@material-ui/core';

import { EditorContext } from '../../Context/EditorContext';

const useStyles = makeStyles(theme => ({
    articleContainer: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    paper: {
        width: '70%',
        padding: 30,
    },
}));

const ArticleSelection = () => {

    const holder = useContext(EditorContext);

    const classes = useStyles();

    return (

        <Grid container>

            <Grid item xs={12}>

                <Button>
                    <Typography>
                        Make New Article
                    </Typography>
                </Button>

            </Grid>


            <Divider />

            <Grid className={classes.articleContainer} item xs={12}>

                {
                    holder.articleList

                        ?

                    holder.articleList.map(article => {
                        return (

                            <Paper className={classes.paper} key={article.title}>

                                <Typography variant="h2">
                                    {article.title}
                                </Typography>

                                <img style={{width: '100%', height: 'auto'}} src={article.jumbotron} alt={article.title} />

                                <Typography variant="subtitle1">
                                    {article.date}
                                </Typography>


                            </Paper>
                        )
                    })

                        :

                    ''
                }

            </Grid>

        </Grid>
    );
};

export default ArticleSelection;
