import React, { useState, createContext, useEffect } from 'react';

export const EditorContext = createContext();

const intialPreview = {
    text: '',
    bold: false,
    italic: false,
    underline: false,
    color: 'black',
    highlight: false,
    font: 'Roboto',
    justify: 'left',
    textStyle: 'body1',
    marginTop: 40,
    marginBottom: 0,
    isText: true,
    isImage: false,
};

const initialImg = {
    isImage: true,
    img: '',
    alt: '',
    height: '',
    width: '',
    marginTop: 40,
    marginBottom: 0,
};

export const EditorProvider = (props) => {

    const [editMode, setEditMode] = useState(false);
    const [articleList, setArticleList] = useState([]);

    const [sectionMode, setSectionMode] = useState({ el: 'title' });
    const [title, setTitle] = useState({ ...intialPreview });
    const [jumbotron, setJumbotron] = useState('');
    const [currentBody, setCurrentBody] = useState(0);
    const [body, setBody] = useState([ { ...intialPreview } ]);

    useEffect(() => {
        fetch('/api/editors/getList')
            .then(res => res.json())
            .then((result) => {
                setArticleList(result)
            })
            .catch((error) => {});
    }, [] );

    const handleMode = () => {
        setEditMode(!editMode);
    };

    const editArticle = index => {
        setTitle(articleList[index].title);
        setJumbotron(articleList[index].jumbotron);
        setBody(articleList[index].body);
        setEditMode(!editMode);
    };

    const editBodyEl = index => {
        setCurrentBody(index);
    };

    const newBodyEl = () => {
        const newBody = body;
        newBody.push({ ...intialPreview });

        setCurrentBody( newBody.length - 1 );
        setBody([ ...newBody ]);
    };

    const handleSectionMode = (newSection) => {
        switch (true) {
            case newSection.el === 'title' && newSection.el !== sectionMode.el:
                setSectionMode(newSection);
                break;

            case newSection.el === 'jumbotron' && newSection.el !== sectionMode.el:
                setSectionMode(newSection);
                break;

            case newSection.index !== sectionMode.index:
                editBodyEl(newSection.index);
                setSectionMode(newSection);
                break;

            default:
                return
        }
    };

    const handleSubmit = data => {
        fetch('/api/editors/newSection',{
            method: 'POST',
            body: JSON.stringify(data),
            headers: {'Content-Type': 'application/json'},
        })
            .then(res => res.json())
            .then((result) => {
                setTitle(result.title);
                setJumbotron(result.jumbotron);
                setBody(result.body);
            })
            .catch(error => {})
    };

    const handleBody = ( input, index ) => {
        let newBody = body;
        newBody[index].text = input;
        setBody([ ...newBody ]);
    };

    const newBody = () => {
        let newBody = body;
        newBody.push({ ...intialPreview });
        setBody([ ...newBody ])
    };

    const newImgEl = () => {
        const newBody = body;
        newBody.push({ ...initialImg });
        setBody([ ...newBody ]);
    };

    const handleInput = ( e, section ) => {
        let input = e.target.value;
        switch (section.El) {
            case 'title':
                setTitle({ ...title, text: input });
                break;
            case 'jumbotron':
                setJumbotron(input);
                break;
            case 'body':
                handleBody(input, section.index);
                break;
            default:
                return
        }
    };

    const handleArticle = () => {
        let section = {
            sectionMode: sectionMode,
            title: title,
            jumbotron: jumbotron,
            body: body,
        };
        handleSubmit(section);
    };

    const handleFont = ( newFont ) => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, font: newFont });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index].font = newFont;
                setBody([ ...newBody ]);
                break;
            default:
                break;
        }
    };

    const handleStyling = (style) => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, [style]: !title[style] });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index][style] = !newBody[sectionMode.index][style];    
                setBody([ ...newBody ]);   
                break;
            default:
                break;
        }
    };

    const handleJustify = justification => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, justify: justification });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index].justify = justification;    
                setBody([ ...newBody ]);
                break;
            default:
                break;
        }
    };

    const handleTextStyle = (style) => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, textStyle: style });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index].textStyle = style;    
                setBody([ ...newBody ]);
                break;
            default:
                break;
        }
    };

    const handleMarginTop = (e) => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, marginTop: e.target.value });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index].marginTop = e.target.value;
                setBody([ ...newBody ]);
                break;
            default:
                return
        }
    };

    const handleMarginBottom = (e) => {
        switch (sectionMode.el) {
            case 'title':
                setTitle({ ...title, marginBottom: e.target.value });
                break;
            case 'body':
                let newBody = body;
                newBody[sectionMode.index].marginBottom = e.target.value;
                setBody([ ...newBody ]);
                break;
            default:
                return
        }
    };

    return (
        <EditorContext.Provider
            value={{
                handleInput,
                title,
                jumbotron,
                currentBody,
                body,
                handleStyling,
                handleFont,
                handleJustify,
                handleTextStyle,
                handleMarginTop,
                handleMarginBottom,
                handleArticle,
                editMode,
                editArticle,
                editBodyEl,
                newBodyEl,
                handleMode,
                articleList,
                sectionMode,
                handleSectionMode,
                newBody,
            }}
        >

            {props.children}

        </EditorContext.Provider>
    );
};
