import React, { useState } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';

import { Paper, Typography, TextField, Button, Icon, Snackbar, SnackbarContent } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    paper: {
        width: '60%',
        padding: 40,
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
    },
    inputs: {
        width: '100%',
        marginTop: 20,
    },
    submit: {
        width: '100%',
        padding: 10,
        marginTop: 20,
        color: 'rgb(255, 255, 255)',
    },
    loginSignup: {
        width: '100%',
        padding: 10,
        marginTop: 20,
    },
    error: {
        backgroundColor: theme.palette.error.dark,
    },
    success: {
        backgroundColor: green[600],
    },
    message: {
        display: 'flex',
        alignItems: 'center',
    },
}));

const Login = () => {

    const classes = useStyles();

    const [signup, setSignup] = useState(false);
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [success, setSuccess] = useState({
        open: false,
        message: '',
    });
    const [error, setError] = useState({
        open: false,
        message: '',
    });

    const closeError = () => { setError({ open: false, message: '' }); };
    const closeSuccess = () => { setSuccess({ open: false, message: 'Article Saved!' }); };

    const handleSubmit = () => {
        if (signup) {
            const data = {
                username,
                email,
                password,
            };
            fetch('/api/users/signup', {
                method: 'POST',
                body: JSON.stringify(data),
                headers: { 'Content-Type': 'application/json' },
            })
                .then(res => res.json())
                .then((result) => { setSuccess({ open: true, message: 'Sign up successful!' }); })
                .catch((error) => { setError({ open: true, message: error }); });
        } else {
            const data = {
                email,
                password,
            };
            fetch('/api/users/login', {
                method: 'POST',
                body: JSON.stringify(data),
                headers: { 'Content-Type': 'application/json' },
            })
                .then(res => res.json())
                .then((result) => {
                    if (result.error) { return setError({ open: true, message: result.message }); }
                    return setSuccess({ open: true, message: 'Login Successful!' });
                })
                .catch((error) => { setError({ open: true, message: error }); });
        }
    };

    if (signup) {
        return (
            <Paper className={classes.paper}>

                <Typography variant="h2">
                    Sign Up
                </Typography>

                <TextField
                    className={classes.inputs}
                    variant="outlined"
                    label="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />

                <TextField
                    className={classes.inputs}
                    variant="outlined"
                    label="Email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />

                <TextField
                    className={classes.inputs}
                    variant="outlined"
                    label="Password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />

                <Button
                    className={classes.submit}
                    onClick={handleSubmit}
                    variant="contained"
                >
                    <Typography variant="h6">
                        Submit
                    </Typography>
                </Button>

                <Button
                    className={classes.loginSignup}
                    onClick={() => setSignup(!signup)}
                    variant="contained"
                >
                    <Typography variant="body1">
                        Already Signed Up?
                    </Typography>
                </Button>

                <Snackbar
                    anchorOrigin={{
                        vertical: 'top',
                        horizontal: 'center',
                    }}
                    open={success.open}
                    autoHideDuration={6000}
                    onClose={closeSuccess}
                >
                    <SnackbarContent
                        className={classes.success}
                        message={
                            <Typography className={classes.message}>

                                <Icon style={{ marginRight: 10 }}>
                                    error
                                </Icon>

                                {success.message}

                            </Typography>
                        }
                        action={
                            <Button onClick={closeSuccess}>
                                <Icon>close</Icon>
                            </Button>
                        }
                    />
                </Snackbar>

                <Snackbar
                    anchorOrigin={{
                        vertical: 'top',
                        horizontal: 'center',
                    }}
                    open={error.open}
                    autoHideDuration={6000}
                    onClose={closeError}
                >
                    <SnackbarContent
                        className={classes.error}
                        message={
                            <Typography className={classes.message}>

                                <Icon style={{ marginRight: 10 }}>
                                    error
                                </Icon>

                                {error.message}

                            </Typography>
                        }
                        action={
                            <Button onClick={closeError}>
                                <Icon>close</Icon>
                            </Button>
                        }
                    />
                </Snackbar>

            </Paper>
        );    
    } else {
        return (
            <Paper className={classes.paper}>

                <Typography variant="h2">
                    Login
                </Typography>

                <TextField
                    className={classes.inputs}
                    variant="outlined"
                    label="Email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />

                <TextField
                    className={classes.inputs}
                    variant="outlined"
                    label="Password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />

                <Button
                    className={classes.submit}
                    onClick={handleSubmit}
                    variant="contained"
                >
                    <Typography variant="h6">
                        Submit
                    </Typography>
                </Button>

                <Button
                    className={classes.loginSignup}
                    onClick={() => setSignup(!signup)}
                    variant="contained"
                >
                    <Typography variant="body1">
                        Not Signed Up?
                    </Typography>
                </Button>

            </Paper>
        );
    }
};

export default Login;
