import React, { Fragment, useContext } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Button, Icon } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const useStyles = makeStyles(theme => ({
    container: {
        width: 100,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    }
}));

const Styling = () => {

    const classes = useStyles();

    const holder = useContext(EditorContext);
    const styling = holder.styling;

    return (

        <Fragment>

            <Button disabled={styling.bold} onClick={() => handleStyling('bold')}>
                <Icon>format_bold</Icon>
            </Button>
            <Button disabled={styling.italic} onClick={() => handleStyling('italic')}>
                <Icon>format_italic</Icon>
            </Button>
            <Button disabled={styling.underline} onClick={() => handleStyling('underline')}>
                <Icon>format_underline</Icon>
            </Button>
            <Button disabled={styling.color} onClick={() => handleStyling('color')}>
                <Icon>format_color_text</Icon>
            </Button>
            <Button disabled={styling.highlight} onClick={() => handleStyling('highlight')}>
                <Icon>highlight</Icon>
            </Button>

        </Fragment>
    );
};

export default Styling;
