import React, { Fragment, useContext } from 'react';

import { Typography, TextField, Icon } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Margin = () => {

    const holder = useContext(EditorContext);

    const marginTopSwitch = () => {
        console.log('TOP')
        switch (holder.sectionMode.el) {
            case 'body' || 'image':
                return <TextField
                            disabled={marginBottomSwitch('disable')}
                            style={{ width: 'auto' }}
                            value={holder.body[holder.sectionMode.index].marginTop}
                            onChange={holder.handleMarginTop}
                        />;
            case 'title' || 'jumbotron':
                return <TextField
                            disabled={marginBottomSwitch('disable')}
                            style={{ width: 'auto' }}
                            value={holder[holder.sectionMode.el].marginTop}
                            onChange={holder.handleMarginTop}
                        />;
            default:
                return <Icon>lock</Icon>;
        }
    };

    const marginBottomSwitch = () => {
        console.log('BOTTOM')
        switch (holder.sectionMode.el) {
            case 'body' || 'image':
                return <TextField
                            style={{ width: 'auto' }}
                            value={holder.body[holder.sectionMode.index].marginBottom}
                            onChange={holder.handleMarginBottom}
                        />;
            case 'title' || 'jumbotron':
                return <TextField
                            style={{ width: 'auto' }}
                            value={holder[holder.sectionMode.el].marginBottom}
                            onChange={holder.handleMarginBottom}
                        />;
            default:
                return <Icon>lock</Icon>;
        }
    };

    return (
        <Fragment>

            <Typography>
                Margin Top:
            </Typography>

            {marginTopSwitch()}

            <Typography>
                Margin Bottom:
            </Typography>

            {marginBottomSwitch()}

        </Fragment>
    );
};

export default Margin;
