import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Button } from '@material-ui/core';

import { Parallax, Background } from 'react-parallax';

const useStyles = makeStyles(theme => ({
    containers: {
        width: '100vw', 
        height: '80vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    elContainers: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    imgs: {
        width: '80%', 
        height: 'auto', 
        borderRadius: '4px',
    },
    infoText: {
        width: '80%',
    },
    button: {
        color: 'white',
        padding: 30,
        transition: 'all 0.4s ease',
        background: 'linear-gradient(242deg, rgba(195, 195, 195, 0.02) 0%, rgba(195, 195, 195, 0.02) 16.667%,rgba(91, 91, 91, 0.02) 16.667%, rgba(91, 91, 91, 0.02) 33.334%,rgba(230, 230, 230, 0.02) 33.334%, rgba(230, 230, 230, 0.02) 50.001000000000005%,rgba(18, 18, 18, 0.02) 50.001%, rgba(18, 18, 18, 0.02) 66.668%,rgba(163, 163, 163, 0.02) 66.668%, rgba(163, 163, 163, 0.02) 83.33500000000001%,rgba(140, 140, 140, 0.02) 83.335%, rgba(140, 140, 140, 0.02) 100.002%),linear-gradient(152deg, rgba(151, 151, 151, 0.02) 0%, rgba(151, 151, 151, 0.02) 16.667%,rgba(11, 11, 11, 0.02) 16.667%, rgba(11, 11, 11, 0.02) 33.334%,rgba(162, 162, 162, 0.02) 33.334%, rgba(162, 162, 162, 0.02) 50.001000000000005%,rgba(171, 171, 171, 0.02) 50.001%, rgba(171, 171, 171, 0.02) 66.668%,rgba(119, 119, 119, 0.02) 66.668%, rgba(119, 119, 119, 0.02) 83.33500000000001%,rgba(106, 106, 106, 0.02) 83.335%, rgba(106, 106, 106, 0.02) 100.002%),linear-gradient(11deg, rgba(245, 245, 245, 0.01) 0%, rgba(245, 245, 245, 0.01) 16.667%,rgba(23, 23, 23, 0.01) 16.667%, rgba(23, 23, 23, 0.01) 33.334%,rgba(96, 96, 96, 0.01) 33.334%, rgba(96, 96, 96, 0.01) 50.001000000000005%,rgba(140, 140, 140, 0.01) 50.001%, rgba(140, 140, 140, 0.01) 66.668%,rgba(120, 120, 120, 0.01) 66.668%, rgba(120, 120, 120, 0.01) 83.33500000000001%,rgba(48, 48, 48, 0.01) 83.335%, rgba(48, 48, 48, 0.01) 100.002%),linear-gradient(27deg, rgba(106, 106, 106, 0.03) 0%, rgba(106, 106, 106, 0.03) 14.286%,rgba(203, 203, 203, 0.03) 14.286%, rgba(203, 203, 203, 0.03) 28.572%,rgba(54, 54, 54, 0.03) 28.572%, rgba(54, 54, 54, 0.03) 42.858%,rgba(75, 75, 75, 0.03) 42.858%, rgba(75, 75, 75, 0.03) 57.144%,rgba(216, 216, 216, 0.03) 57.144%, rgba(216, 216, 216, 0.03) 71.42999999999999%,rgba(39, 39, 39, 0.03) 71.43%, rgba(39, 39, 39, 0.03) 85.71600000000001%,rgba(246, 246, 246, 0.03) 85.716%, rgba(246, 246, 246, 0.03) 100.002%),linear-gradient(317deg, rgba(215, 215, 215, 0.01) 0%, rgba(215, 215, 215, 0.01) 16.667%,rgba(72, 72, 72, 0.01) 16.667%, rgba(72, 72, 72, 0.01) 33.334%,rgba(253, 253, 253, 0.01) 33.334%, rgba(253, 253, 253, 0.01) 50.001000000000005%,rgba(4, 4, 4, 0.01) 50.001%, rgba(4, 4, 4, 0.01) 66.668%,rgba(183, 183, 183, 0.01) 66.668%, rgba(183, 183, 183, 0.01) 83.33500000000001%,rgba(17, 17, 17, 0.01) 83.335%, rgba(17, 17, 17, 0.01) 100.002%),linear-gradient(128deg, rgba(119, 119, 119, 0.03) 0%, rgba(119, 119, 119, 0.03) 12.5%,rgba(91, 91, 91, 0.03) 12.5%, rgba(91, 91, 91, 0.03) 25%,rgba(45, 45, 45, 0.03) 25%, rgba(45, 45, 45, 0.03) 37.5%,rgba(182, 182, 182, 0.03) 37.5%, rgba(182, 182, 182, 0.03) 50%,rgba(243, 243, 243, 0.03) 50%, rgba(243, 243, 243, 0.03) 62.5%,rgba(162, 162, 162, 0.03) 62.5%, rgba(162, 162, 162, 0.03) 75%,rgba(190, 190, 190, 0.03) 75%, rgba(190, 190, 190, 0.03) 87.5%,rgba(148, 148, 148, 0.03) 87.5%, rgba(148, 148, 148, 0.03) 100%),linear-gradient(90deg, rgb(185, 139, 80),rgb(176, 26, 6))',
        '&:hover': {
            transform: 'scale(1.03)',
            backgroundPosition: '0px -50px',
        },
    },
}));

const ImageList = () => {

    const classes = useStyles();

    return (

        <Grid container>

            <Grid className={classes.containers} item xs={6}>

                <Parallax 
                    strength={-400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '40%'
                    }}
                >

                    <Background className={classes.elContainers} style={{ width: '100%', height: '100%' }}>
                        <img className={classes.imgs} src={`http://www.daveallenphotography.com/gallery/l_summer_laurel_blue_ridge_parkway_nc.jpg`} />
                    </Background>

                </Parallax>

            </Grid>
            <Grid style={{ width: '100vw', height: '80vh' }} item xs={6}>

                <Parallax 
                    strength={400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '70%'
                    }}
                >

                    <Background style={{ width: '100%', height: '100%' }}>
                        <Typography className={classes.infoText} variant="h5" align="center">
                            The Blue Ridge Parkway is a ribbon of road that weaves through 
                            the region’s vibrant living traditions. From Cherokee life and 
                            agricultural history to Southern Appalachian crafts and music, 
                            the Parkway’s 469 miles through Virginia and North Carolina offer 
                            many ways to explore rich traditions.
                        </Typography>
                    </Background>

                </Parallax>

            </Grid>
            <Grid style={{ width: '100vw', height: '80vh' }} item xs={6}>

                <Parallax 
                    strength={400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '70%'
                    }}
                >

                    <Background className={classes.elContainers}>
                        <Typography className={classes.infoText} variant="h5" align="center">
                            Spanning the southern and central Appalachians, the Blue Ridge Parkway 
                            offers an exceptional glimpse of the regional flora and fauna. It is 
                            world-renowned for its biodiversity. The Parkway covers a wide range 
                            of habitats along the Appalachian Mountains, and some of these habitats 
                            are exceptionally rare. Visitors encounter unsurpassed diversity of 
                            climate, vegetation, wildlife and geological features.
                        </Typography>
                    </Background>

                </Parallax>

            </Grid>
            <Grid className={classes.containers} item xs={6}>

                <Parallax 
                    strength={-400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '40%'
                    }}
                >

                    <Background className={classes.elContainers} style={{ width: '100%', height: '100%' }}>
                        <img className={classes.imgs} src={`https://assets.simpleviewinc.com/simpleview/image/upload/c_fill,h_474,q_75,w_640/v1/clients/newyorkstate/AD_2015_122_44de426d-7824-4c67-8331-32143c83903e.jpg`} />
                    </Background>

                </Parallax>

            </Grid>
            <Grid className={classes.containers} item xs={6}>

                <Parallax 
                    strength={-400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '40%'
                    }}
                >

                    <Background className={classes.elContainers} style={{ width: '100%', height: '100%' }}>
                        <img className={classes.imgs} src={`https://www.blueridgemountains.com/imager/s3_amazonaws_com/blueridge-2018/images/1920-width/iStock-134561164_5ac8ae049534b01c104dc4019c5230ff.jpg`} />
                    </Background>

                </Parallax>

            </Grid>
            <Grid style={{ width: '100vw', height: '80vh' }} item xs={6}>

                <Parallax 
                    strength={400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '70%'
                    }}
                >

                    <Background className={classes.elContainers}>
                        <Typography className={classes.infoText} variant="h5" align="center">
                            Millions of visitors enjoy the Blue Ridge Parkway each year. 
                            Some think of it as just a motor road or a pleasant drive. But 
                            the Parkway is also a place of varied and significant natural 
                            resources.
                        </Typography>
                    </Background>

                </Parallax>

            </Grid>

            <Grid style={{ height: '20vh' }} item xs={12}>

            <Parallax 
                    strength={-400}
                    style={{
                        width: '100%',
                        height: '100%'
                    }}
                    bgStyle={{
                        width: '100%',
                        top: '70%'
                    }}
                >

                    <Background className={classes.elContainers}>

                        <a style={{textDecoration: 'none'}} href={`https://www.blueridgeparkway.org/`} target="_blank">
                            <Button className={classes.button}>

                                <Typography variant="h4" align="center">
                                    Visit the official Blue Ridge Parkway website!
                                </Typography>

                            </Button>
                        </a>

                    </Background>

                </Parallax>


            </Grid>



        </Grid>

    );
};

export default ImageList;
