import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, MobileStepper, Button, Icon } from '@material-ui/core';

const useStyles = makeStyles(theme => ({

}));

const Stepper = () => {

    const classes = useStyles();
    const theme = useTheme();
    const [activeStep, setActiveStep] = React.useState(0);

    const handleNext = () => {
      setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    const handleBack = () => {
      setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    return (
        <MobileStepper
        variant="progress"
        steps={6}
        position="static"
        activeStep={activeStep}
        className={classes.root}
        nextButton={
          <Button size="small" onClick={handleNext} disabled={activeStep === 5}>
            Next
            {theme.direction === 'rtl' ? <Icon>keyboard_arrow_left</Icon> : <Icon>keyboard_arrow_right</Icon> }
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === 'rtl' ? <Icon>keyboard_arrow_right</Icon> : <Icon>keyboard_arrow_left</Icon> }
            Back
          </Button>
        }
      />
    );
};

export default Stepper;
