import React, { Fragment } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import { Paper, Typography } from '@material-ui/core';

import SlideShow from './SlideShow';


const useStyles = makeStyles(theme => ({
    paper: {
        width: '70%',
        margin: '80px 0px',
        padding: '20px 0px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexWrap: 'wrap',
        background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
    },
    typoContainer: {
        // background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
        width: '80%',
        padding: 20,
    },
}));

function Body() {

    const classes = useStyles();

    return (
        <Fragment>

            {/* <Paper className={classes.paper}> */}

                <SlideShow />

                <Paper className={classes.typoContainer}>

                    <Typography style={{margin: '0px 50px'}} align="center" color="textPrimary">
                        {`Welcome to our little piece of paradise in the mountains 
                        of Boone NC! A Mountain Dream Cabin is far enough from 
                        civilization to be secluded, yet close enough to town & 
                        local attractions to be more than convenient. Located only 
                        10 quick miles outside of Boone, NC in the exclusive log 
                        cabin community of Twin Hollows. A Mountain Dream has every 
                        amenity to make your stay as comfortable, pleasant and stress 
                        free as you could ever imagine.`}
                    </Typography>

                </Paper>

            {/* </Paper> */}

        </Fragment>
    );
}

export default Body;
