import React, { useContext, useEffect, Fragment } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography } from '@material-ui/core';

import { EditorContext } from '../../Context/EditorContext';

const Preview = () => {

    const holder = useContext(EditorContext);

    const useStyles = makeStyles(theme => ({
        typo: {
            fontFamily: `${holder.font}, Helvetica, Arial, sans-serif`,
            fontWeight: holder.styling.bold ? 'bold' : 'normal',
            fontStyle: holder.styling.italic ? 'italic' : 'normal',
            textDecoration: holder.styling.underline ? 'underline' : 'none',
            color: holder.styling.color,
            backgroundColor: holder.styling.highlight ? '#ffff00' : 'none'
        }
    }));

    const classes = useStyles();

    return (

        <Grid container>

            <Grid item xs={12}>



                <Typography>
                    {holder.preview.title}
                </Typography>

                <img style={{ width: '100%', height: 'auto' }} src={holder.preview.jumbotron} alt={holder.preview.title} />

                { 
                    holder.preview.body.map((section, index) => {
                        return (
                            <Typography
                                style={{
                                    fontFamily: `${section.font}, Helvetica, Arial, sans-serif`,
                                    fontWeight: section.bold ? 'bold' : 'normal',
                                    fontStyle: section.italic ? 'italic' : 'normal',
                                    textDecoration: section.underline ? 'underline' : 'none',
                                    color: section.color,
                                    backgroundColor: section.highlight ? '#ffff00' : 'none'                        
                                }}
                                variant={section.textStyle}
                                align={section.justify}
                                key={index}
                            >
                                {section.text}
                            </Typography>
                        )
                    })
                }

            </Grid>

        </Grid>
    );
};

export default Preview;
