import React, { useContext } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography } from '@material-ui/core';

import { Parallax, Background } from 'react-parallax';

import { MediaContext } from '../../../Context/MediaQuery';

import SlideShow from './SlideShow';



function Body() {

    const media = useContext(MediaContext);

    const useStyles = makeStyles(theme => ({
        container: {
            height: '100vh',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'flex-end',
            flexWrap: 'wrap',
        },
        parallaxContainers: {
            width: '100%', 
            height: '50%',
            '& .react-parallax-background-children': {
                top: media.xs ? '30%'
                :
                media.sm ? '30%'
                    :
                media.md ? '35%'
                    :
                media.lg ? '40%'
                    :
                '40%'
            },
        },
        parallaxText: {
            width: '100%', 
            height: '50%',
            '& .react-parallax-background-children': {
                top: media.xs ? '30%'
                :
                media.sm ? '35%'
                    :
                media.md ? '55%'
                    :
                media.lg ? '45%'
                    :
                '50%',
                width: '100%', 
            },
        },
        paper: {
            width: '70%',
            margin: '80px 0px',
            padding: '20px 0px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            flexWrap: 'wrap',
            background: 'linear-gradient(45deg, #ffffff 30%, #ece9e6 90%)'
        },
        typoContainer: {
            width: '80%',
            padding: 20,
        },
        textContainer: {
            height: '50%',
            width: '100%',
            padding: 20,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            color: 'white',
        },
        slideContainer: {
            height: '100%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        },
    }));

    const classes = useStyles();

    const getStrength = () => {

        switch (true) {

            case media.md:
                return '400';
            case media.lg:
                return '-150';
        }
    };

    return (

        <Grid className={classes.container} container>

            <Grid className={classes.textContainer} item lg={6} md={12}>     

                <Parallax className={classes.parallaxText} strength={getStrength()}>

                    <Background>

                        <Typography variant="h5" align="center">
                            {`A Mountain Dream Cabin is far enough from 
                            civilization to be secluded, yet close enough to town & 
                            local attractions to be more than convenient. Located only 
                            10 quick miles outside of Boone, NC in the exclusive log 
                            cabin community of Twin Hollows. A Mountain Dream has every 
                            amenity to make your stay as comfortable, pleasant and stress 
                            free as you could ever imagine.`}
                        </Typography>

                    </Background>

                </Parallax>

            </Grid>
            <Grid className={classes.slideContainer} item lg={6} md={12}>

                <Parallax 
                    className={classes.parallaxContainers}
                    strength={350}
                >

                    <Background>

                        <SlideShow />

                    </Background>

                </Parallax>

            </Grid>

        </Grid>
    );
}

export default Body;
