import React, { useState, createContext, useEffect } from 'react';

export const EditorContext = createContext();

const initialStyling = {
    bold: false,
    italic: false,
    underline: false,
    color: 'black',
    highlight: false,
}

export const EditorProvider = props => {

    const [editMode, setEditMode] = useState(false);
    const [articleList, setArticleList] = useState('');

    const [input, setInput] = useState('');

    const [sectionMode, setSectionMode] = useState('title');
    const [title, setTitle] = useState('');
    const [jumbotron, setJumbotron] = useState('');
    const [body, setBody] = useState([]);

    const [styling, setStyling] = useState(initialStyling);
    const [font, setFont] = useState( 'Roboto' );
    const [justify, setJustify] = useState('left');
    const [textStyle, setTextStyle] = useState('body1');

    useEffect(() => {

        fetch('/api/editors/getList')
        .then(res => res.json())
        .then(result => setArticleList(result))
        .catch(error => console.log(error));

    }, [] )

    useEffect(() => {
        console.log(sectionMode)
    }, [sectionMode])

    const handleMode = () => {
        setEditMode(!editMode);
    }

    const handleSectionMode = (newSection) => {
        switch (newSection) {

            case 'title':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            case 'jumbotron':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            case 'body':
            case newSection !== sectionMode:
                setSectionMode(newSection);
                break;

            default:
                return
        }
    };

    const handleSubmit = data => {


        fetch('/api/editors/newSection',{
            method: 'POST',
            body: JSON.stringify(data),
            headers: {'Content-Type': 'application/json'}
        })
        .then(res => res.json())
        .then((result) => {
            console.log(result);
        })
        .catch(error => {
            console.log(error);
        })
    };

    const handleInput = (e) => {
        setInput(e.target.value);
    };

    const handleArticle = () => {
        let newBody = body;

        let section = {
            text: input,
            font: font,
            justify: justify,
            textStyle: textStyle,
            bold: styling.bold,
            italic: styling.italic,
            underline: styling.underline,
            color: styling.color,
            highlight: styling.highlight
        }

        handleSubmit(section);

        newBody.push(section);

        setBody(newBody);
        setInput('');
        setStyling(initialStyling);
        setFont('Roboto');
        setJustify('left');
        setTextStyle('body1');
    };

    const handleFont = ( newFont ) => {
        setFont(newFont);
    };

    const handleStyling = ( style, color ) => {
        switch (style) {
            case 'bold':
                setStyling({ ...styling, bold: !styling.bold });
                break; 

            case 'italic':
                setStyling({ ...styling, italic: !styling.italic });
                break;

            case 'underline':
                setStyling({ ...styling, underline: !styling.underline });
                break;

            case 'color':
                setStyling({ ...styling, color: color });
                break;

            case 'highlight':
                setStyling({ ...styling, highlight: !styling.highlight });
                break; 

            default:
                return
        };
    };

    const handleJustify = justification => {
        setJustify(justification);
    };

    const handleTextStyle = style => {
        setTextStyle(style);
    };

    return (
        <EditorContext.Provider
            value={{
                handleInput,
                input,
                styling,
                handleStyling,
                font,
                handleFont,
                justify,
                handleJustify,
                textStyle,
                handleTextStyle,
                body,
                handleArticle,
                editMode,
                handleMode,
                articleList,
                sectionMode,
                handleSectionMode
            }}
        >
            {props.children}
        </EditorContext.Provider>
    );
};
