import React, { Fragment, useContext } from 'react';

import { Typography, TextField } from '@material-ui/core';

import { EditorContext } from '../../../Context/EditorContext';

const Margin = () => {

    const holder = useContext(EditorContext);

    return (
        <Fragment>

            <Typography>
                Margin Top:
            </Typography>

            <TextField
                style={{ width: 'auto' }}
                value={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder[holder.sectionMode.el][holder.sectionMode.index].marginTop
                        :
                    holder[holder.sectionMode.el].marginTop
                }
                onChange={holder.handleMarginTop}
            />

            <Typography>
                Margin Bottom:
            </Typography>

            <TextField
                style={{ width: 'auto' }}
                value={
                    holder.sectionMode.el === 'body' || holder.sectionMode.el === 'image'
                        ?
                    holder[holder.sectionMode.el][holder.sectionMode.index].marginBottom
                        :
                    holder[holder.sectionMode.el].marginBottom
                }
                onChange={holder.handleMarginBottom}
            />

        </Fragment>
    );
};

export default Margin;
