import React, { useState } from 'react';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import { Grid, MobileStepper, Button, Icon } from '@material-ui/core';

const getStepContent = (step) => {
    switch (step) {
      case 0:
        return `The Blue Ridge Parkway is a ribbon of road that weaves through 
        the region’s vibrant living traditions. From Cherokee life and 
        agricultural history to Southern Appalachian crafts and music, 
        the Parkway’s 469 miles through Virginia and North Carolina offer 
        many ways to explore rich traditions.`;
      case 1:
        return `Spanning the southern and central Appalachians, the Blue Ridge Parkway 
        offers an exceptional glimpse of the regional flora and fauna. It is 
        world-renowned for its biodiversity. The Parkway covers a wide range 
        of habitats along the Appalachian Mountains, and some of these habitats 
        are exceptionally rare. Visitors encounter unsurpassed diversity of 
        climate, vegetation, wildlife and geological features.`;
      case 2:
        return `Millions of visitors enjoy the Blue Ridge Parkway each year. 
        Some think of it as just a motor road or a pleasant drive. But 
        the Parkway is also a place of varied and significant natural 
        resources.`;
      default:
        return 'Unknown step';
    }
}

const useStyles = makeStyles(theme => ({

}));

const Stepper = () => {

    const classes = useStyles();
    const theme = useTheme();

    const steps = getSteps();

    const [activeStep, setActiveStep] = useState(0);

    const handleNext = () => {
      setActiveStep(prevActiveStep => prevActiveStep + 1);
    };

    const handleBack = () => {
      setActiveStep(prevActiveStep => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };

    return (

        <Grid alignItems="flex-start" container>


            <Grid className={classes.descContainer} item xs={12}>

                <Paper className={classes.paper}>

                    <Typography className={classes.brpDesc} variant="h5" align="center">

                    </Typography>

                </Paper>

            </Grid>


        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>All steps completed</Typography>
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button
                disabled={activeStep === 0}
                onClick={handleBack}
                className={classes.backButton}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
            <MobileStepper
                variant="progress"
                steps={6}
                position="static"
                activeStep={activeStep}
                className={classes.root}
                nextButton={
                <Button size="small" onClick={handleNext} disabled={activeStep === 5}>
                    Next
                    {theme.direction === 'rtl' ? <Icon>keyboard_arrow_left</Icon> : <Icon>keyboard_arrow_right</Icon> }
                </Button>
                }
                backButton={
                <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                    {theme.direction === 'rtl' ? <Icon>keyboard_arrow_right</Icon> : <Icon>keyboard_arrow_left</Icon> }
                    Back
                </Button>
                }
            />

    </Grid>
    );
};

export default Stepper;
